function showPasswordToggle(){[].slice.call(document.querySelectorAll(".toggle-password")).map(function(group){const passwordInput=group.querySelector(":scope > [type='password']");const togglePasswordButton=group.querySelector(":scope button.d-none");togglePasswordButton.classList.remove("d-none");togglePasswordButton.innerHTML='<i class="bi bi-eye"></i>';togglePasswordButton.addEventListener("click",togglePassword);function togglePassword(){if(passwordInput.type==="password"){passwordInput.type="text";togglePasswordButton.setAttribute("aria-label","Hide password.");togglePasswordButton.innerHTML='<i class="bi bi-eye-slash"></i>'}else{passwordInput.type="password";togglePasswordButton.setAttribute("aria-label","Show password as plain text. "+"Warning: this will display your password on the screen.");togglePasswordButton.innerHTML='<i class="bi bi-eye"></i>'}}})}
